$('#arguments').submit(function() {
  $.ajax({
    type: 'POST',
    url: CC.tool_json_url,
    data: $(this).serialize(),
    success: function(d) {
      location.href = d.result_url;
    },
    error: function(jqXHR) {
      console.log(jqXHR.responseText);
    }
  });
  return false;
});

$('[data-toggle="popover"]').popover({
  'trigger': 'click hover focus'
});
