$('#arguments').submit(function() {
  var $o = $(this).trigger('updated.cc'),
      $submit_button = $o.find('[type=submit]').prop('disabled', true),
      submit_button_text = $submit_button.text();
  $submit_button.text('Running...');
  $.ajax({
    type: 'POST',
    url: CC.tool_json_url,
    data: $o.serialize(),
    success: function(d) {
      location.href = d.result_url;
    },
    error: function(jqXHR) {
      $o.trigger('failed.cc', jqXHR.responseJSON);
      $submit_button.prop('disabled', false).text(submit_button_text);
    }
  });
  return false;
});

$('[data-toggle="popover"]').popover({
  'trigger': 'click hover focus'
});
